import cors from 'cors';
import dotenv from 'dotenv';
import express from 'express';
import mongoose from 'mongoose';
import swaggerUi from 'swagger-ui-express';

import allRoutes from './routes';
import apiDocumentation from '../swagger.json';

dotenv.config();
const app = express();
app.use(express.json());
app.use(cors());

mongoose.connect(process.env.DB, { useNewUrlParser: true, useUnifiedTopology: true });
mongoose.connection.on('error', (err) => { console.log(`Error at mongo: ${err}`); });
mongoose.connection.on('connected', () => { console.log('Connected to mongoDB'); });

app.use('/api', allRoutes);
app.use(`/api/documentation`, swaggerUi.serve, swaggerUi.setup(apiDocumentation));
app.get('**', (req, res) => res.status(200).json({ status: 200, message: `Welcome To uPaged Backend` }));

app.listen(process.env.PORT, () => { console.log('Server Started on', process.env.PORT); });

export default app;

import Router from 'express';
import jobRouter from './jobRoute';

const router = Router();
router.use('/jobs', jobRouter);

export default router;

import Router from 'express';
import jobController from '../controllers/jobController';
import paginateMiddleware from '../middlewares/paginateMiddleware';

const jobRouter = Router();
jobRouter
  .get('/select-jobs', jobController.selectJobs, paginateMiddleware.paginateData);

export default jobRouter;

import { INTERNAL_SERVER_ERROR, NOT_FOUND } from 'http-status';

import responseHelper from '../helpers/responseHelper';
import pagination from '../helpers/paginateHelper';
import jobHelper from '../helpers/jobHelper';

/**
* This class contains all methods (functions) required to handle
* Select all jobs function.
*/
class JobController {
  /**
     * Handle selectJobs.
     * @param {object} req user request.
     * @param {object} res data response.
     * @param {object} next data move.
     * @returns {object} return obeject of jobs.
     */
  static async selectJobs(req, res, next) {
    try {
      const { start, end, pages, skip, paginate } = await pagination.paginateData(req.query);
      const fetchedData = await jobHelper.findJobs(skip, start);
      const countedData = await jobHelper.countJobs();

      const allDatata = fetchedData;
      const countAllData = countedData;

      if (countAllData < 1) {
        responseHelper.handleError(NOT_FOUND, 'Jobs not found at the moment');
        return responseHelper.response(res);
      }

      req.data = { allDatata, countAllData, start, end, pages, skip, paginate };
      next();
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }
}

export default JobController;

/**
* This class contains all methods (functions) required to handle
* handleSuccess function.
* handleError function.
* response function.
*/
class ResponseHelpers {
  /**
   * Handle Success Response.
   * @param {integer} statusCode status code.
   * @param {integer} message message.
   * @param {object} data data.
   * @returns {null} .
   */
  static handleSuccess(statusCode, message, data) {
    this.statusCode = statusCode;
    this.message = message;
    this.data = data;
    this.type = 'success';
  }

  /**
   * Handle Error Response.
   * @param {integer} statusCode status code.
   * @param {integer} message message.
   * @returns {null} .
   */
  static handleError(statusCode, message) {
    this.statusCode = statusCode;
    this.message = message;
    this.type = 'error';
  }

  /**
   * Handle response output.
   * @param {object} res response.
   * @returns {object} response.
   */
  static response(res) {
    if (this.type === 'success') {
      return res.status(this.statusCode).json({
        status: this.statusCode,
        message: this.message,
        data: this.data,
      });
    }
    return res.status(this.statusCode).json({
      status: this.statusCode,
      message: this.message,
    });
  }
}

export default ResponseHelpers;

/**
* This Pagination Class contain paginateData method (paginateData function) required to handle
* The method return data.
*/
class Pagination {
  /**
   * Get page number and limit of data needed on single page.
   * @param {integer} page page number.
   * @param {integer} limit value to be found.
   * @returns {object} paginate tools.
   */
  static paginateData({ page, limit }) {
    const paginate = {};
    const skip = Number(limit || Number.MAX_SAFE_INTEGER);
    const pages = Number(page || 1);
    const start = Number((pages - 1) * skip);
    const end = page * skip;
    return {
      start, end, pages, skip, paginate
    };
  }
}

module.exports = Pagination;

import JobSchema from '../models/jobSchema';

/**
* This class contains all methods (functions) required to handle
* Select all jobs function.
*/
class JobHelper {
  /**
   * findjobs from Jobs table.
   * @param {integer} skip limitation point.
   * @param {integer} start start point.
   * @returns {object} of all jobs.
   */
  static async findJobs(skip, start) {
    const data = await JobSchema.find().skip(start).limit(skip);
    return data;
  }

  /**
   * countjobs from Jobs table.
   * @returns {number} of all jobs counted.
   */
  static async countJobs() {
    const data = await JobSchema.countDocuments();
    return data;
  }
}

export default JobHelper;

import mongoose from 'mongoose';

const Schema = mongoose.Schema;
const JobSchema = new Schema({
  shift: { type: String, required: [true, 'shift is required'] },
  workingHours: { type: String, required: [true, 'workingHours is required'] },
  position: { type: String, required: [true, 'position is required'] },
  salary: { type: String, required: [true, 'salary is required'] },
  company: { type: String, required: [true, 'company is required'] },
  review: { type: String, required: [true, 'review is required'] },
  street: { type: String, required: [true, 'street is required'] },
  address: { type: String, required: [true, 'address is required'] },
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

export default mongoose.model('Jobs', JobSchema);

import { INTERNAL_SERVER_ERROR, OK } from 'http-status';
import responseHelper from '../helpers/responseHelper';
/**
* This class contains method (functions) required to handle
* paginateData function.
*/
class PaginateData {
  /**
     * Handle paginateData.
     * @param {object} req user request.
     * @param {object} res data response.
     * @returns {object} returned object of data.
     */
  static paginateData(req, res) {
    try {
      req.data.paginate.paginate = req.data.allDatata;
      if (req.data.start > 0) {
        req.data.paginate.Previous = {
          page: req.data.pages - 1,
          limit: req.data.skip,
        };
      } if (req.data.end < req.data.countAllData) {
        req.data.paginate.Next = {
          page: req.data.pages + 1,
          limit: req.data.skip,
        };
      }

      const data = req.data.paginate;
      responseHelper.handleSuccess(OK, `Page ${req.data.pages || null} Selected successfully`, data);
      return responseHelper.response(res);
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }
}
export default PaginateData;
